import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import HeroSection from '../components/HeroSection';

const Home = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => {
      setIsOpen(!isOpen);
  };

  //parallax scroll
  const [offsetY, setOffsetY] = useState(0);
  const handleScroll = () => setOffsetY(window.pageYOffset);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const movingParallax = () => {
    <>
      <div className = "parallax_content">
        <h2>"Our mission is to advocate <b>to improve</b> public health in the local community through education, outreach, and practical solutions."</h2>
      </div>
    </>
  };

  return (
    <>
      <Navbar toggle={toggle} />
      <HeroSection
        style={{ transform: `translateY(${offsetY * 0.5}px)` }}
      />
      <div className='all_parallax'>{movingParallax()}</div>
      <div className = "parallax_content">
        <h2>"Our mission is to advocate <b>to improve</b> public health in the local community through education, outreach, and practical solutions."</h2>
      </div>
    </>
  );
};

export default Home;
