import React, { Component } from 'react';
import { useState } from 'react';

import { PurposeImg, AllPurposeImgs } from './PurposeElements';
import advocacyImg from '../../assets/advocacy.PNG'
import supportImg from '../../assets/support.PNG'
import aidImg from '../../assets/aid.PNG'

class Purpose2 extends Component {

  state = {
    visible: false
  };

  render() {
      return (
        <div className="showAndHide">

        <AllPurposeImgs>
          <PurposeImg>
              <button onClick={() => {
                this.setState({ visible: !this.state.visible });
              }}>
                <img src = {advocacyImg} alt='People sit in a group' />
                <p>Perseverance</p>
              </button>
              {this.state.visible ? <div>We will not be complacent with our work. But instead we will continue in our commitment to advocacy and education in improving public health, building upon previous skills, knowledge and creativity.</div> : null}
          </PurposeImg>

          <PurposeImg>
              <button onClick={() => {
                this.setState({ visible: !this.state.visible });
              }}>
                <img src = {advocacyImg} alt='People sit in a group' />
                <p>Compassion</p>
              </button>
              {this.state.visible ? <div>We will not be complacent with our work. But instead we will continue in our commitment to advocacy and education in improving public health, building upon previous skills, knowledge and creativity.</div> : null}
          </PurposeImg>

          <PurposeImg>
              <button onClick={() => {
                this.setState({ visible: !this.state.visible });
              }}>
                <img src = {advocacyImg} alt='People sit in a group' />
                <p>Open-Mindedness</p>
              </button>
              {this.state.visible ? <div>We will not be complacent with our work. But instead we will continue in our commitment to advocacy and education in improving public health, building upon previous skills, knowledge and creativity.</div> : null}
          </PurposeImg>
        </AllPurposeImgs>

        </div>
      );

  }
}

export default Purpose2;

