import React, {Component} from 'react';
import { PurposeImg, AllPurposeImgs } from './PurposeElements';
import { Parallax } from 'react-parallax';
import advocacyImg from '../../assets/advocacy.PNG'
import supportImg from '../../assets/support.PNG'
import aidImg from '../../assets/aid.PNG'

const Purpose = () => {

  const handleClick = () => {
    console.log("Hello World!");
  }

  class expandText extends Component {
    state = {
      visible: true
    };

  render() {
  return (
    <>
      <Parallax strength={800}>
        <div className = "home_header"><h2>Our Purpose</h2></div>
      </Parallax>
      <AllPurposeImgs>
        <PurposeImg>
          <div className="expandText">
            <button onClick={() => {
              this.setState({ visible: false });
            }}>
              <img src = {advocacyImg} alt='People sit in a group' />
              <p>Perseverance</p>
            </button>
            {this.state.visible ? <p>We will not be complacent with our work. But instead we will continue in our commitment to advocacy and education in improving public health, building upon previous skills, knowledge and creativity.</p> : null}
          </div>
        </PurposeImg>

        <PurposeImg>
          <button onClick={handleClick}>
            <img src = {supportImg} alt='Hands piled together in middle of group' />
            <p>Compassion</p>
          </button>
        </PurposeImg>

        <PurposeImg>
          <button onClick={handleClick}>
            <img src = {aidImg} alt='Two hands clasped from opposite sides.' />
            <p>Open-mindedness</p>
          </button>
        </PurposeImg>
      </AllPurposeImgs>
    </>
  );

}
}
};

export default Purpose;
